export function injectOriginToBlankPostMessages(window, origin) {
    // workaround for https://github.com/jsdom/jsdom/issues/2745
    // if no origin exists, replace with the wallet url
    window.addEventListener('message', function (event) {
        if (event.origin === '') {
            event.stopImmediatePropagation();
            var eventWithOrigin = new MessageEvent('message', {
                data: event.data,
                origin: origin,
            });
            window.dispatchEvent(eventWithOrigin);
        }
    });
}
